import React, { Component } from 'react';
import TextField from '@mui/material/TextField';
import Button from '@mui/material/Button';
import Grid from '@mui/material/Grid';
import SendIcon from '@mui/icons-material/Send';

export default class MultilineTextFields extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: ''
    };
  }

  render() {
    return (
<Grid container justifyContent="center" size='50%'>
  <Grid padding={2} item xs={8}>
  <TextField
          id="outlined-multiline-static"
          label="Insert your text here"
          onChange={(e) => this.setState({value: e.target.value})} 
          multiline
          rows={10}
          fullWidth
        />
  </Grid>
  <Grid container justifyContent="center">
  <Grid padding={2}>
  <Button variant="contained" endIcon={<SendIcon />} onClick={() => {console.log(this.state.value)}}>Summarize</Button>
  </Grid>
  </Grid>
  </Grid>
  )}}
