import React, { Component } from 'react';
import TextField from '@mui/material/TextField';
import IconButton from '@mui/material/IconButton';
import Button from '@mui/material/Button';
import Grid from '@mui/material/Grid';
import SendIcon from '@mui/icons-material/Send';
import DeleteIcon from '@mui/icons-material/Delete';

export default class MultilineTextFields extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
      output: ''
    };
  }

  render() {
    return (
<Grid container justifyContent="center" size='50%'>
  <Grid padding={2} item xs={8}>
  <TextField
          id="outlined-multiline-static"
          label="Insert your text here"
          onChange={(e) => this.setState({value: e.target.value})} 
          multiline
          rows={8}
          fullWidth
        />
  </Grid>
  <Grid container justifyContent="center">
  <Grid padding={2}container spacing={5}>
  <IconButton aria-label="delete">
  <DeleteIcon />
  </IconButton>
  <Button variant="contained" endIcon={<SendIcon />} onClick={() => this.setState({output: this.state.value})}>Summarize</Button>
  </Grid>
  </Grid>
  <Grid container justifyContent="center">
  <Grid padding={2} item xs={8}>
  <TextField
              label="Insert your text here"
              multiline
              value={this.state.output}
              fullWidth
              rows={8}
            />
  </Grid>
  </Grid>
  </Grid>
  )}}
